import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchJobsRequest, setFilters, clearFilters, setPage } from '../../store/actions';
import JobCard from './JobCard';
import JobFilters from './JobFilters';
import Pagination from '../common/Pagination';
import './JobList.css';

const JobList = () => {
  const dispatch = useDispatch();
  const { jobs, loading, pagination, filters } = useSelector((state) => state.jobs);
  const [searchParams, setSearchParams] = useState({
    title: '',
    location: '',
    type: '',
    status: 'active',
  });

  useEffect(() => {
    // Initialize with current filters from Redux state
    setSearchParams(filters);
  }, [filters]);

  useEffect(() => {
    // Fetch jobs when component mounts or filters change
    const params = {
      ...searchParams,
      page: pagination.page,
      limit: pagination.limit,
    };
    dispatch(fetchJobsRequest(params));
  }, [dispatch, searchParams, pagination.page, pagination.limit]);

  const handleFilterChange = (newFilters) => {
    setSearchParams(prev => ({ ...prev, ...newFilters }));
    dispatch(setFilters(newFilters));
  };

  const handleClearFilters = () => {
    const defaultFilters = {
      title: '',
      location: '',
      type: '',
      status: 'active',
    };
    setSearchParams(defaultFilters);
    dispatch(clearFilters());
  };

  const handlePageChange = (page) => {
    dispatch(setPage(page));
  };

  return (
    <div className="job-list-container">
      <div className="container">
        <div className="job-list-header">
          <h1>Job Opportunities</h1>
          <p>Find your next career opportunity from our curated list of jobs</p>
        </div>

        <div className="job-list-content">
          <aside className="job-filters-sidebar">
            <JobFilters
              filters={searchParams}
              onFilterChange={handleFilterChange}
              onClearFilters={handleClearFilters}
            />
          </aside>

          <main className="job-list-main">
            {loading ? (
              <div className="loading">
                <div className="spinner"></div>
              </div>
            ) : (
              <>
                <div className="job-list-results">
                  <div className="results-header">
                    <h2>
                      {pagination.total > 0
                        ? `${pagination.total} job${pagination.total !== 1 ? 's' : ''} found`
                        : 'No jobs found'}
                    </h2>
                    {pagination.total > 0 && (
                      <p>
                        Showing {((pagination.page - 1) * pagination.limit) + 1} to{' '}
                        {Math.min(pagination.page * pagination.limit, pagination.total)} of{' '}
                        {pagination.total} results
                      </p>
                    )}
                  </div>

                  {jobs.length > 0 ? (
                    <div className="jobs-grid">
                      {jobs.map((job) => (
                        <JobCard key={job.id} job={job} />
                      ))}
                    </div>
                  ) : (
                    <div className="no-results">
                      <div className="no-results-icon">üîç</div>
                      <h3>No jobs found</h3>
                      <p>Try adjusting your search criteria or filters</p>
                      <button
                        onClick={handleClearFilters}
                        className="btn btn-primary"
                      >
                        Clear Filters
                      </button>
                    </div>
                  )}
                </div>

                {pagination.pages > 1 && (
                  <Pagination
                    currentPage={pagination.page}
                    totalPages={pagination.pages}
                    onPageChange={handlePageChange}
                  />
                )}
              </>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default JobList;
