import React, { useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchJobRequest } from '../../store/actions';
import './JobDetail.css';

const JobDetail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { currentJob, loading, error } = useSelector((state) => state.jobs);
  const { isAuthenticated } = useSelector((state) => state.auth);

  useEffect(() => {
    dispatch(fetchJobRequest(id));
  }, [dispatch, id]);

  const formatSalary = (min, max) => {
    if (!min && !max) return 'Salary not specified';
    if (min && max) return `$${min.toLocaleString()} - $${max.toLocaleString()}`;
    if (min) return `From $${min.toLocaleString()}`;
    if (max) return `Up to $${max.toLocaleString()}`;
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      active: { text: 'Active', class: 'status-active' },
      inactive: { text: 'Inactive', class: 'status-inactive' },
      closed: { text: 'Closed', class: 'status-closed' },
    };
    return statusMap[status] || { text: status, class: 'status-default' };
  };

  const getTypeBadge = (type) => {
    const typeMap = {
      'full-time': { text: 'Full Time', class: 'type-fulltime' },
      'part-time': { text: 'Part Time', class: 'type-parttime' },
      'contract': { text: 'Contract', class: 'type-contract' },
      'internship': { text: 'Internship', class: 'type-internship' },
    };
    return typeMap[type] || { text: type, class: 'type-default' };
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="container">
          <div className="error-card">
            <h2>Error</h2>
            <p>{error}</p>
            <Link to="/jobs" className="btn btn-primary">
              Back to Jobs
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (!currentJob) {
    return (
      <div className="error-container">
        <div className="container">
          <div className="error-card">
            <h2>Job Not Found</h2>
            <p>The job you're looking for doesn't exist or has been removed.</p>
            <Link to="/jobs" className="btn btn-primary">
              Back to Jobs
            </Link>
          </div>
        </div>
      </div>
    );
  }

  const statusBadge = getStatusBadge(currentJob.status);
  const typeBadge = getTypeBadge(currentJob.type);

  return (
    <div className="job-detail-container">
      <div className="container">
        <div className="job-detail-header">
          <button onClick={() => navigate(-1)} className="back-btn">
            ‚Üê Back
          </button>
        </div>

        <div className="job-detail-card">
          <div className="job-detail-header-content">
            <div className="job-title-section">
              <h1>{currentJob.title}</h1>
              <div className="job-badges">
                <span className={`badge ${statusBadge.class}`}>
                  {statusBadge.text}
                </span>
                <span className={`badge ${typeBadge.class}`}>
                  {typeBadge.text}
                </span>
              </div>
            </div>
            <div className="job-company">
              <h2>{currentJob.company}</h2>
            </div>
          </div>

          <div className="job-meta">
            <div className="meta-item">
              <span className="meta-icon">üìç</span>
              <span>{currentJob.location}</span>
            </div>
            <div className="meta-item">
              <span className="meta-icon">üí∞</span>
              <span>{formatSalary(currentJob.salary_min, currentJob.salary_max)}</span>
            </div>
            <div className="meta-item">
              <span className="meta-icon">üìÖ</span>
              <span>Posted {new Date(currentJob.created_at).toLocaleDateString()}</span>
            </div>
            {currentJob.posted_by_name && (
              <div className="meta-item">
                <span className="meta-icon">üë§</span>
                <span>Posted by {currentJob.posted_by_name}</span>
              </div>
            )}
          </div>

          <div className="job-description">
            <h3>Job Description</h3>
            <div className="description-content">
              {currentJob.description.split('\n').map((paragraph, index) => (
                <p key={index}>{paragraph}</p>
              ))}
            </div>
          </div>

          <div className="job-actions">
            {isAuthenticated && currentJob.status === 'active' ? (
              <Link to={`/jobs/${currentJob.id}/apply`} className="btn btn-primary btn-large">
                Apply Now
              </Link>
            ) : (
              <div className="apply-disabled">
                {!isAuthenticated ? (
                  <p>Please <Link to="/login">login</Link> to apply for this job.</p>
                ) : (
                  <p>This job is no longer accepting applications.</p>
                )}
              </div>
            )}
            <Link to="/jobs" className="btn btn-secondary">
              Browse More Jobs
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobDetail;
