import React from 'react';
import { Link } from 'react-router-dom';
import './ApplicationCard.css';

const ApplicationCard = ({ application }) => {
  const getStatusBadge = (status) => {
    const statusMap = {
      pending: { text: 'Pending', class: 'status-pending' },
      reviewed: { text: 'Reviewed', class: 'status-reviewed' },
      accepted: { text: 'Accepted', class: 'status-accepted' },
      rejected: { text: 'Rejected', class: 'status-rejected' },
    };
    return statusMap[status] || { text: status, class: 'status-default' };
  };

  const statusBadge = getStatusBadge(application.status);

  return (
    <div className="application-card">
      <div className="application-header">
        <h3 className="job-title">
          <Link to={`/jobs/${application.job.id}`}>
            {application.job.title}
          </Link>
        </h3>
        <span className={`badge ${statusBadge.class}`}>
          {statusBadge.text}
        </span>
      </div>

      <div className="application-company">
        <strong>{application.job.company}</strong>
      </div>

      <div className="application-meta">
        <div className="meta-item">
          <span className="meta-icon">üìç</span>
          <span>{application.job.location}</span>
        </div>
        <div className="meta-item">
          <span className="meta-icon">üí∞</span>
          <span>
            {application.job.salary_min && application.job.salary_max
              ? `$${application.job.salary_min.toLocaleString()} - $${application.job.salary_max.toLocaleString()}`
              : 'Salary not specified'}
          </span>
        </div>
        <div className="meta-item">
          <span className="meta-icon">üìÖ</span>
          <span>Applied {new Date(application.created_at).toLocaleDateString()}</span>
        </div>
      </div>

      <div className="application-cover-letter">
        <h4>Cover Letter</h4>
        <p>
          {application.cover_letter.length > 150
            ? `${application.cover_letter.substring(0, 150)}...`
            : application.cover_letter}
        </p>
      </div>

      {application.cv_link && (
        <div className="application-cv">
          <h4>CV Link</h4>
          <a href={application.cv_link} target="_blank" rel="noopener noreferrer">
            View CV
          </a>
        </div>
      )}

      <div className="application-footer">
        <Link to={`/applications/${application.id}`} className="btn btn-primary btn-sm">
          View Details
        </Link>
        <Link to={`/jobs/${application.job.id}`} className="btn btn-secondary btn-sm">
          View Job
        </Link>
      </div>
    </div>
  );
};

export default ApplicationCard;
