import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchApplicationsRequest } from '../../store/actions';
import ApplicationCard from './ApplicationCard';
import './ApplicationList.css';

const ApplicationList = () => {
  const dispatch = useDispatch();
  const { applications, loading, pagination } = useSelector((state) => state.applications);
  const { user } = useSelector((state) => state.auth);

  useEffect(() => {
    dispatch(fetchApplicationsRequest());
  }, [dispatch]);

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="application-list-container">
      <div className="container">
        <div className="application-list-header">
          <h1>My Applications</h1>
          <p>Track your job applications and their status</p>
        </div>

        {applications.length > 0 ? (
          <div className="applications-grid">
            {applications.map((application) => (
              <ApplicationCard key={application.id} application={application} />
            ))}
          </div>
        ) : (
          <div className="no-applications">
            <div className="no-applications-icon">üìù</div>
            <h3>No Applications Yet</h3>
            <p>You haven't applied to any jobs yet. Start browsing jobs to apply!</p>
            <a href="/jobs" className="btn btn-primary">
              Browse Jobs
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

export default ApplicationList;
