import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import './JobCard.css';

const JobCard = ({ job }) => {
  const { isAuthenticated } = useSelector((state) => state.auth);

  const formatSalary = (min, max) => {
    if (!min && !max) return 'Salary not specified';
    if (min && max) return `$${min.toLocaleString()} - $${max.toLocaleString()}`;
    if (min) return `From $${min.toLocaleString()}`;
    if (max) return `Up to $${max.toLocaleString()}`;
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      active: { text: 'Active', class: 'status-active' },
      inactive: { text: 'Inactive', class: 'status-inactive' },
      closed: { text: 'Closed', class: 'status-closed' },
    };
    return statusMap[status] || { text: status, class: 'status-default' };
  };

  const getTypeBadge = (type) => {
    const typeMap = {
      'full-time': { text: 'Full Time', class: 'type-fulltime' },
      'part-time': { text: 'Part Time', class: 'type-parttime' },
      'contract': { text: 'Contract', class: 'type-contract' },
      'internship': { text: 'Internship', class: 'type-internship' },
    };
    return typeMap[type] || { text: type, class: 'type-default' };
  };

  const statusBadge = getStatusBadge(job.status);
  const typeBadge = getTypeBadge(job.type);

  return (
    <div className="job-card">
      <div className="job-header">
        <h3 className="job-title">
          <Link to={`/jobs/${job.id}`}>{job.title}</Link>
        </h3>
        <div className="job-badges">
          <span className={`badge ${statusBadge.class}`}>
            {statusBadge.text}
          </span>
          <span className={`badge ${typeBadge.class}`}>
            {typeBadge.text}
          </span>
        </div>
      </div>

      <div className="job-company">
        <strong>{job.company}</strong>
      </div>

      <div className="job-location">
        üìç {job.location}
      </div>

      <div className="job-salary">
        üí∞ {formatSalary(job.salary_min, job.salary_max)}
      </div>

      <div className="job-description">
        {job.description.length > 150
          ? `${job.description.substring(0, 150)}...`
          : job.description}
      </div>

      <div className="job-footer">
        <div className="job-meta">
          <span className="job-posted">
            Posted {new Date(job.created_at).toLocaleDateString()}
          </span>
        </div>
        <div className="job-actions">
          <Link to={`/jobs/${job.id}`} className="btn btn-primary btn-sm">
            View Details
          </Link>
          {isAuthenticated && job.status === 'active' && (
            <Link to={`/jobs/${job.id}/apply`} className="btn btn-success btn-sm">
              Apply Now
            </Link>
          )}
        </div>
      </div>
    </div>
  );
};

export default JobCard;
